# core change: Loss-Adaptive LR Scaling
class LossAdaptiveScaler:
    def __init__(self, ema_beta=0.98, gamma=0.7, f_min=0.5, f_max=1.5):
        self.ema_beta = ema_beta
        self.gamma = gamma
        self.f_min = f_min; self.f_max = f_max
        self.ema_loss = None
    def scale(self, loss):
        loss_val = loss.detach().item()
        if self.ema_loss is None:
            self.ema_loss = loss_val
        else:
            self.ema_loss = self.ema_beta * self.ema_loss + (1-self.ema_beta)*loss_val
        factor = (self.ema_loss / (loss_val+1e-8)) ** self.gamma
        return max(self.f_min, min(self.f_max, factor))

